import { ethers } from "ethers";
import React, { useEffect, useState } from "react";
import './styles/App.css';
import twitterLogo from './assets/twitter-logo.svg';
import myEpicNft from './utils/MyEpicNFT.json';

// Constants„ÇíÂÆ£Ë®Ä„Åô„Çã: const„Å®„ÅØÂÄ§Êõ∏„ÅçÊèõ„Åà„ÇíÁ¶ÅÊ≠¢„Åó„ÅüÂ§âÊï∞„ÇíÂÆ£Ë®Ä„Åô„ÇãÊñπÊ≥ï„Åß„Åô„ÄÇ
const TWITTER_HANDLE = 'dokidoki_konbu';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;
const OPENSEA_LINK = '';
const TOTAL_MINT_COUNT = 50;

const App = () => {
    const [currentAccount, setCurrentAccount] = useState("");
    console.log("currentAccount: ", currentAccount);

    const checkIfWalletIsConected = async () => {
        const { ethereum } = window;
        if (!ethereum) {
            console.log("Make sure you have MetaMask!");
            return;
          } else {
            console.log("We have the ethereum object", ethereum);
        }

        const accounts = await ethereum.request({ method: "eth_accounts" });

        if (accounts.length !== 0) {
            const account = accounts[0];
            console.log("Found an authorized account:", account);
            setCurrentAccount(account);
            setupEventListener();
        } else {
            console.log("No authorized account found");
        }
    }

    const connectWallet = async () => {
        try {
            const { ethereum } = window;
            if (!ethereum) {
                alert("Get MetaMask!");
                return;
            }

            const accounts = await ethereum.request({ method: "eth_requestAccounts" });
            console.log("Connected", accounts[0]);
            setCurrentAccount(accounts[0]);

            setupEventListener();
        } catch (error) {
            console.log(error);
        }
    }

    const   setupEventListener = () => {
        try {
            const { ethereum } = window;
            if (ethereum) {
                const provider = new ethers.providers.Web3Provider(ethereum);
                const signer = provider.getSigner();
                const connectedContract = new ethers.Contract(
                  CONTRACT_ADDRESS,
                  myEpicNft.abi,
                  signer
                );
                // Event „Åå„ÄÄemit „Åï„Çå„ÇãÈöõ„Å´„ÄÅ„Ç≥„É≥„Éà„É©„ÇØ„Éà„Åã„ÇâÈÄÅ‰ø°„Åï„Çå„ÇãÊÉÖÂ†±„ÇíÂèó„ÅëÂèñ„Å£„Å¶„ÅÑ„Åæ„Åô„ÄÇ
                connectedContract.on("NewEpicNFTMinted", (from, tokenId) => {
                  console.log(from, tokenId.toNumber());
                  alert(
                    `„ÅÇ„Å™„Åü„ÅÆ„Ç¶„Ç©„É¨„ÉÉ„Éà„Å´ NFT „ÇíÈÄÅ‰ø°„Åó„Åæ„Åó„Åü„ÄÇOpenSea „Å´Ë°®Á§∫„Åï„Çå„Çã„Åæ„ÅßÊúÄÂ§ß„Åß10ÂàÜ„Åã„Åã„Çã„Åì„Å®„Åå„ÅÇ„Çä„Åæ„Åô„ÄÇNFT „Å∏„ÅÆ„É™„É≥„ÇØ„ÅØ„Åì„Å°„Çâ„Åß„Åô: https://testnets.opensea.io/assets/${CONTRACT_ADDRESS}/${tokenId.toNumber()}`
                  );
                });
            } else {
                console.log("Ethereum object doesn't exist!");
            }
        } catch (error) {
            console.log(error);
        }
    }

    const askContractToMintNft = async () => {
        const CONTRACT_ADDRESS = "0xf7CDf131ADe33313C8CFF3609FC2dDE949099451";
        try {
            const { ethereum } = window;
            if (ethereum) {
                const provider = new ethers.providers.Web3Provider(ethereum);
                const signer = provider.getSigner();
                const connectedContract = new ethers.Contract(
                  CONTRACT_ADDRESS,
                  myEpicNft.abi,
                  signer
                );
                console.log("Going to pop wallet now to pay gas...");
                let nftTxn = await connectedContract.makeAnEpicNFT();
                console.log("Mining...please wait.");
                await nftTxn.wait();
                console.log(
                    `Mined, see transaction: https://rinkeby.etherscan.io/tx/${nftTxn.hash}`
                  );
            } else {
                console.log("Ethereum object doesn't exist!");
            }
        } catch (error) {
            console.log(error);
        }
    }

    const renderNotConnectedContainer = () => (
        <button  onClick={ connectWallet } className="cta-button connect-wallet-button">
        Connect to Wallet
        </button>
    );

    useEffect(() => checkIfWalletIsConected(), []);

    return (
        <div className="App">
        <div className="container">
            <div className="header-container">
            <p className="header gradient-text">My NFT Collection</p>
            <p className="sub-text">
                „ÅÇ„Å™„Åü„Å†„Åë„ÅÆÁâπÂà•„Å™ NFT „Çí Mint „Åó„Çà„ÅÜüí´
            </p>
            {currentAccount === "" ? (
                renderNotConnectedContainer()
            ) : (
                <button onClick={askContractToMintNft} className="cta-button connect-wallet-button">
                Mint NFT
                </button>
            )}
            </div>
            <div className="footer-container">
            <img alt="Twitter Logo" className="twitter-logo" src={twitterLogo} />
            <a
                className="footer-text"
                href={TWITTER_LINK}
                target="_blank"
                rel="noreferrer"
            >{`built on @${TWITTER_HANDLE}`}</a>
            </div>
        </div>
        </div>
    );
};

export default App;
